        function renderTaskCard(task) {
            return `
                <div class="task-card priority-${task.priority}">
                    <div class="task-priority ${task.priority}">${task.priority}</div>
                    <h4 class="task-title">${task.title}</h4>
                    <div class="task-meta">
                        <span><i class="fas fa-user"></i> ${task.assignee}</span>
                        <span><i class="fas fa-calendar"></i> ${formatDate(task.due)}</span>
                    </div>
                </div>
            `;
        }

        // Schedules data array
        let schedules = [];
        let daysOff = []; // Array to store employee days off: { employeeId, date, store }
        let currentWeekStart = getWeekStart(new Date());
        let draggedShift = null;

        // Shift types configuration
        const SHIFT_TYPES = {
            opening: {
                name: 'Opening',
                icon: 'fa-sun',
                color: '#f59e0b',
                gradient: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)',
                defaultStart: '09:00',
                defaultEnd: '17:00'
            },
            closing: {
                name: 'Closing',
                icon: 'fa-moon',
                color: '#6366f1',
                gradient: 'linear-gradient(135deg, #6366f1 0%, #4f46e5 100%)',
                defaultStart: '14:00',
                defaultEnd: '22:00'
            }
        };

        function getWeekStart(date) {
            const d = new Date(date);
            const day = d.getDay();
            const diff = d.getDate() - day;
            return new Date(d.setDate(diff));
        }

        function getWeekDates(startDate) {
            const dates = [];
            for (let i = 0; i < 7; i++) {
                const date = new Date(startDate);
                date.setDate(startDate.getDate() + i);
                dates.push(date);
            }
            return dates;
        }

        function formatDateKey(date) {
            // Use local date components to avoid timezone offset issues
            const year = date.getFullYear();
            const month = String(date.getMonth() + 1).padStart(2, '0');
            const day = String(date.getDate()).padStart(2, '0');
            return `${year}-${month}-${day}`;
        }

        /**
         * Convert number to ordinal (1st, 2nd, 3rd, etc.)
         */
        function getOrdinal(n) {
            const s = ["th", "st", "nd", "rd"];
            const v = n % 100;
            return n + (s[(v - 20) % 10] || s[v] || s[0]);
        }

