                                <div>
                                    <div style="font-weight: 500; color: var(--text-primary);">${training.fileName}</div>
                                    <div style="font-size: 12px; color: var(--text-muted);">${training.fileSize ? formatFileSize(training.fileSize) : 'Unknown size'}</div>
                                </div>
